$('#login').click((e) => {
  e.preventDefault(); const r = {}; $.each($('#loginForm').serializeArray(),(e,a) => {
    r[a.name] = a.value;
  }),$.ajax({ type:'POST',url:'../api/login',data:r,success:function (e){
    location.reload();
  },error:function (e){
    errorAlert(e.responseJSON.message);
  } });
});
